INSERT INTO `proposal` (`id`, `name`, `body`, `image`, `user_id`, `created_at`) VALUES
(4, 'Сбер представил GigaChat 2.0 — сильнейшую нейросетевую модель на русском языке', 'Пользователи сохраняют доступ к моделям первого поколения и могут протестировать GigaChat 2.0, прежде чем перейти на новый модельный ряд.\r\nНа базе GigaChat 2.0 компании смогут создавать более продуктивных автономных помощников (AI-агенты), способных рассуждать и самостоятельно решать сложные многокомпонентные задачи.\r\nДля разработки агентов на Python и JS допустимо использовать SDK LangChain, с которым GigaChat полностью совместим.\r\nПо данным независимого бенчмарка MERA для русского языка, GigaChat 2 MAX занимает первое место среди AI-моделей.', 'z0hHqMe7UIfm8iRcJwQUt3gfjNEnOBIscjC053u0.jpeg', 12, '2025-03-13 23:32:25'),
(5, 'The Verge: функция цепочки сообщений появилась в WhatsApp', 'Функция цепочки сообщений, которая упростит отслеживание ответов в чатах, появилась в WhatsApp (принадлежит компании Meta, которая признана в России экстремистской и запрещена).\r\nВместо того, чтобы пролистывать чат в поисках разрозненных ответов, новая функция будет группировать все ответы на определенное сообщение в отдельную ветку по аналогии с тредами на форумах или в X (Twitter).\r\nНа данный момент функция находится на стадии разработки и недоступна для тестирования.\r\nЦепочки сообщений будут доступны в личных и групповых чатах, сообществах и каналах, утверждает портал.', '800x400.jpg', 12, '2025-03-13 23:33:15'),
(6, 'Идет распродажа. В РФ резко подешевел iPhone 15', 'По данным Hi-Tech Mail.ru, стоимость базовой модели iPhone 15, выпущенной в 2023 году, достигла минимального значения за все время продаж.\r\nТакже самые дешевые смартфоны Apple обнаружили с доставкой из-за рубежа — в этом случае покупателю придется заплатить пошлину, которая составит около 10 процентов от стоимости гаджета.\r\nНа задней панели гаджета расположена двойная камера с объективами разрешением 48 и 12 мегапикселей, спереди — 12-мегапиксельная фронтальная камера.\r\nТакже телефон вышел с аккумулятором емкостью 3349 миллиампер-часов, биометрическим сенсором Face ID, платежным сервисом Apple Pay.', '800x400 (1).jpg', 12, '2025-03-13 23:34:04');

